import logo from './logo.svg';
import './App.css';

function App() {
  const handleClick = () => {
    addTask();
    displayTaskList();
  };
  return (
    // <div className="todo-list">
    //   <header className="todo-header">
    //     <img src={logo} className="App-logo" alt="logo" />
    //     <p>
    //       Edit <code>src/App.js</code> and save to reload.
    //     </p>
    //     <a
    //       className="App-link"
    //       href="https://reactjs.org"
    //       target="_blank"
    //       rel="noopener noreferrer"
    //     >
    //       Learn React
    //     </a>
    //   </header>
    // </div>
    <div className="todo-list">
      <header>TO-DO LIST</header>
      <p>Get your to-do list here...</p>
      <input type="text" placeholder="Write your pending task here..." id="tasks-to-add"/>
      <div class="buttons">
          <button class="add-button" id="add-task" onClick={handleClick}>Add to the list</button>
          <button class="reset-button" id="reset-list" onClick={resetList}>Reset List</button>
      </div>
      <div class="task-list">
      </div>
    </div>
  );
}
const taskList = [];
function getValue() { 
  let inputField = document.getElementById("tasks-to-add");
  let value = inputField.value;
  console.log(value);
  return value;
}
function addTask(){
  taskList.push(getValue());
}

function displayTaskList(){
  let newTask = document.createElement("div");
  newTask.className = "task";
  let taskText = document.createElement("h4");
  taskText.innerText = getValue();
  newTask.appendChild(taskText);
  let deleteButton = document.createElement("button");
  deleteButton.className = "delete-button";
  deleteButton.innerHTML = '<span class="material-icons">delete</span>';
  deleteButton.addEventListener('click', deleteTask);
  newTask.appendChild(deleteButton);
  document.querySelector(".task-list").prepend(newTask);
  document.getElementById("tasks-to-add").value = "";
}

function deleteTask(){
  var li=this.parentElement;
  li.parentElement.removeChild(li);
}

function resetList(){
  let taskContainer = document.querySelector(".task-list");
  while (taskContainer.firstChild) {
      taskContainer.removeChild(taskContainer.firstChild);
  }
}

export default App;
