import { useState } from "react";
import "./App.css";

function App() {
  const [taskList, settaskList] = useState([]);
  function getValue() {
    let inputField = document.getElementById("tasks-to-add");
    let value = inputField.value;
    if (value) {
      settaskList((prev) => {
        return [...prev, value];
      });
    } else {
      alert("Please enter a task");
    }
    document.getElementById("tasks-to-add").value = "";
  }

  function resetList() {
    let taskContainer = document.querySelector(".task-list");
    while (taskContainer.firstChild) {
      taskContainer.removeChild(taskContainer.firstChild);
    }
  }
  function getAPIList() {
    fetch('https://jsonplaceholder.typicode.com/posts/1')
      .then((response) => response.json())
      .then((json) => console.log(json));
  }
  return (
    <div className="todo-list">
      <h1>To-Do List</h1>
      <p>Get your to-do list here...</p>
      <input
        type="text"
        placeholder="Write your pending task here..."
        id="tasks-to-add"
      />
      <div className="buttons">
        <button className="add-button" id="add-task" onClick={getValue}>
          Add to the list
        </button>
        <button className="reset-button" id="reset-list" onClick={resetList}>
          Reset List
        </button>
        <button className="api-button" id="api-list" onClick={getAPIList}>
          {" "}
          Get To-Do list from API
        </button>
      </div>
      <div className="task-list">
        {taskList.map((task, index) => (
          <div key={index} className="task">
            <h4>{task}</h4>
            <button
              className="delete-button"
              onClick={() => {
                settaskList((prev) => {
                  return prev.filter((item, i) => i !== index);
                });
              }}
            >
              delete
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
